con:config.script:
def enString = context.expand( '${#TestCase#enString}' )
log.info "English String: $enString"

def ruString = context.expand( '${#TestCase#ruString}' )
log.info "Russian String: $enString"

def ukString = context.expand( '${#TestCase#ukString}' )
log.info "Ukrain String: $ukString"

class CountryToLangValue {
    def static String convert(String country) {        
        def map = ['Russian':'ru', 'English':'en']

        if(!map.containsKey(country))
            throw new IllegalArgumentException("Couldn't find lang for the provided country: $country")
        else
            return map[country]
    }
}

def country = context.expand( '${#TestCase#Country}' )
return CountryToLangValue.convert(country)
